import React, { useState } from 'react';
import { TextField, Button, Typography, Box, CircularProgress, Modal } from '@mui/material';
import { GoogleGenerativeAI } from '@google/generative-ai'; // Importando a biblioteca do Gemini

// Estilos do Modal
const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: 600,  // Tamanho maior para o modal
  height: 600, // Definindo a altura do modal
  bgcolor: 'background.paper',
  border: '2px solid #000',
  boxShadow: 24,
  p: 4,
  overflowY: 'auto', // Permitindo rolagem se o conteúdo for maior
};

const BularioPage = () => {
  const [medicamento, setMedicamento] = useState('Dipirona'); // Medicamento padrão
  const [loading, setLoading] = useState(false); // Estado de carregamento
  const [error, setError] = useState(null); // Estado de erro
  const [open, setOpen] = useState(false); // Controle do modal
  const [medicamentoInfo, setMedicamentoInfo] = useState(''); // Informações do medicamento

  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);

  // Função para formatar as informações do medicamento
  const formatMedicamentoInfo = (info) => {
    return info
      .replace(/(\d+\.)/g, '<strong>$&</strong>') // Deixa os números das listas em negrito
      .replace(/([A-Za-z ]+)(?=:)/g, '<strong>$1</strong>')  // Deixa os títulos (ex: "Classe Terapêutica") em negrito
      .replace(/\*\*/g, '');  // Remove os "**"
  };

  // Função de busca do medicamento e integração com o Gemini
  const handleSearch = async () => {
    if (!medicamento.trim()) {
      setError('Por favor, digite o nome de um medicamento.');
      return;
    }

    setError(null);
    setLoading(true); // Ativa o carregamento
    handleOpen(); // Abre o modal

    try {
      const genAI = new GoogleGenerativeAI('AIzaSyANWW0Tlbheq1AV37hWDIYJLjJv4GaGt_Y'); // Substitua com sua chave API do Gemini
      const model = genAI.getGenerativeModel({ model: 'gemini-1.5-flash' });

      // Prompt que você passará para o modelo de IA
      const prompt = `Conte-me em 20 linhas tudo separadinho em tópicos sobre o medicamento: ${medicamento}.`;

      // Fazendo a chamada à API do Gemini para obter informações sobre o medicamento
      const result = await model.generateContent(prompt);
      const formattedInfo = formatMedicamentoInfo(result.response.text()); // Formata as informações
      setMedicamentoInfo(formattedInfo); // Armazenando as informações no estado
    } catch (error) {
      setError('Erro ao buscar informações do medicamento.');
      console.error('Erro ao buscar medicamento:', error);
    } finally {
      setLoading(false); // Desativa o carregamento
    }
  };

  return (
    <Box sx={{ fontFamily: 'Arial, sans-serif', backgroundColor: '#f9f9f9' }}>
      {/* Header */}
      <header style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '20px 40px', borderBottom: '1px solid #ddd' }}>
        <div style={{ fontSize: '14px', color: '#555' }}>
          <a href="#">Saúde & Bem-Estar</a> | <a href="#">Anvisa</a> | <a href="#">Medicamentos</a>
        </div>
        <div style={{ fontSize: '14px', color: '#555' }}>
          <select style={{ border: 'none', background: 'none', fontSize: '14px', color: '#555', cursor: 'pointer' }}>
            <option>Olá, Usuário</option>
          </select>
        </div>
      </header>

      {/* Main Content */}
      <main style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', padding: '20px 40px', textAlign: 'left', flex: '1' }}>
        <h1 style={{ fontSize: '36px', fontWeight: 'bold', color: '#000' }}>Farmacinha</h1>
        <h2 style={{ fontSize: '22px', color: '#d81b1b', marginTop: '-10px' }}>Saúde & Bem-Estar</h2>
        <h3 style={{ fontSize: '18px', color: '#000', marginTop: '-5px' }}>Cuidando de Você com Clareza e Confiança</h3>

        {/* Barra de Pesquisa */}
        <div style={{ display: 'flex', alignItems: 'center', margin: '20px 0', width: '100%', maxWidth: '600px' }}>
          <input
            type="text"
            placeholder="Buscar bula"
            style={{ flex: '1', padding: '10px', border: '1px solid #ccc', borderRadius: '4px 0 0 4px', fontSize: '16px' }}
            value={medicamento}
            onChange={(e) => setMedicamento(e.target.value)}
          />
          <button
            onClick={handleSearch}
            style={{
              padding: '10px 20px',
              border: 'none',
              backgroundColor: '#000',
              color: '#fff',
              cursor: 'pointer',
              borderRadius: '0 4px 4px 0',
              fontSize: '16px',
            }}
            disabled={loading}
          >
            {loading ? <CircularProgress size={24} sx={{ color: 'white' }} /> : 'Pesquisar'}
          </button>
        </div>

        {/* Informações do Medicamento */}
        <div style={{ width: '100%', maxWidth: '800px', fontSize: '16px', lineHeight: '1.6', marginTop: '20px', color: '#333' }}>
          {error && <Typography variant="body1" sx={{ color: 'red' }}>{error}</Typography>}
          <div dangerouslySetInnerHTML={{ __html: medicamentoInfo }} />
        </div>
      </main>

      {/* Modal com as Informações do Medicamento */}
      <Modal open={open} onClose={handleClose} aria-labelledby="modal-title" aria-describedby="modal-description">
        <Box sx={style}>
          {loading ? (
            <CircularProgress size={60} sx={{ display: 'block', margin: '0 auto' }} />
          ) : (
            <div>
              <Typography variant="h4" sx={{ marginBottom: '20px', fontWeight: 'bold' }}>
                Informações sobre {medicamento}
              </Typography>
              <Typography
                id="modal-description"
                variant="body1"
                sx={{ fontSize: '16px', lineHeight: '1.8', color: '#333', textAlign: 'left', whiteSpace: 'pre-line' }}
                dangerouslySetInnerHTML={{ __html: medicamentoInfo }}
              />
            </div>
          )}
        </Box>
      </Modal>
    </Box>
  );
};

export default BularioPage;
