import React, { useState } from 'react';
import { Button, TextField, Typography, Modal, CircularProgress, Box } from '@mui/material';
import { GoogleGenerativeAI } from '@google/generative-ai';
import { BounceLoader } from 'react-spinners';
import farmaciaImage from '../Media/farmacia.jpeg';  // Caminho da imagem

// Estilo do modal
const customStyles = {
  content: {
    top: "50%",
    left: "50%",
    transform: "translate(-50%, -50%)",
    width: "500px",
    height: "500px",
    padding: "30px",
    backgroundColor: "#fff",
    borderRadius: "8px",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.2)",
    overflowY: "auto",
  },
};

const Home = () => {
  const [medicamento, setMedicamento] = useState(''); // Estado para o nome do medicamento
  const [resultado, setResultado] = useState(null); // Resultado da pesquisa
  const [loading, setLoading] = useState(false); // Carregando
  const [error, setError] = useState(null); // Erros
  const [isOpen, setIsOpen] = useState(false); // Controle do modal

  // Função para abrir o modal
  const openModal = () => setIsOpen(true);

  // Função para fechar o modal
  const closeModal = () => setIsOpen(false);

  // Função para buscar o medicamento com a IA
  const handleSearch = async () => {
    if (!medicamento) {
      setError("Por favor, digite um nome de medicamento.");
      return;
    }
    setError(null);
    setLoading(true);

    try {
      const genAI = new GoogleGenerativeAI("AIzaSyDRhqrsJafsJMUMKo61Sh2aUp4mhWtOvwk"); // Sua chave API
      const model = genAI.getGenerativeModel({ model: "gemini-1.5-flash" });
      const prompt = `Conte-me tudo sobre o medicamento ${medicamento}.`;

      const result = await model.generateContent(prompt);
      setResultado(result.response.text()); // Atualiza o resultado
      openModal(); // Abre o modal
    } catch (error) {
      setError("Erro ao buscar medicamento. Verifique a chave da API.");
      console.error("Erro:", error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={styles.container}>
      {/* Navbar */}
      <header style={styles.navbar}>
        <div style={styles.logo}>
          <Typography variant="h6" style={styles.logoText}>Saúde&<br />Bem-Estar</Typography>
        </div>
        <nav>
          <ul style={styles.navLinks}>
            <li><a href="/login" style={styles.navLink}>Entrar</a></li>
            <li><a href="/cadastro" style={styles.navLink}>Cadastre-se</a></li>
          </ul>
        </nav>
      </header>

      {/* Hero Section */}
      <section style={styles.hero}>
        <Typography variant="h1" style={styles.heroTitle}>Farmacinha</Typography>
        <Typography variant="h2" style={styles.heroSubtitle}>Saúde & Bem-Estar</Typography>
        <Typography variant="h5" style={styles.heroText}>Cuidando de Você com Clareza e Confiança</Typography>
        <Typography variant="h6" style={styles.heroSubtext}>Dúvidas sobre medicamentos ou prescrições? Encontre orientações e respostas com segurança aqui.</Typography>
      </section>

      {/* Barra de Pesquisa */}
      <section style={styles.searchSection}>
        <Typography variant="h4" style={styles.searchTitle}>Qual o remédio você tem interesse em saber?</Typography>
        <TextField
          variant="outlined"
          label="Digite o nome do remédio"
          value={medicamento}
          onChange={(e) => setMedicamento(e.target.value)}
          style={styles.searchInput}
          onKeyPress={(e) => e.key === "Enter" && handleSearch()}
        />
        <Button variant="contained" onClick={handleSearch} style={styles.searchButton}>Buscar</Button>
        {error && <Typography color="error" style={styles.errorText}>{error}</Typography>}
      </section>

      {/* Imagem */}
      <section style={styles.imageSection}>
        <img src={farmaciaImage} alt="Farmácia" style={styles.heroImage} />
      </section>

      {/* Modal com as Informações */}
      <Modal
        isOpen={isOpen}
        onRequestClose={closeModal}
        style={customStyles}
        contentLabel="Informações do Medicamento"
      >
        {loading ? (
          <div style={styles.loaderContainer}>
            <BounceLoader color="#4CAF50" loading={loading} size={60} />
            <Typography variant="h6" style={styles.loaderText}>Estamos buscando informações para você...</Typography>
          </div>
        ) : (
          <div style={styles.modalContent}>
            {resultado ? (
              <>
                <Typography variant="h4" style={styles.modalTitle}>Informações sobre {medicamento}</Typography>
                <Box style={styles.infoBox}>
                  <Typography variant="body1" style={styles.modalText}><strong>Detalhes:</strong> {resultado}</Typography>
                </Box>
              </>
            ) : (
              <Typography variant="body1">Não foi possível encontrar informações sobre este medicamento.</Typography>
            )}
            <Button onClick={closeModal} style={styles.closeButton}>Fechar</Button>
          </div>
        )}
      </Modal>

      {/* Footer */}
      <footer style={styles.footer}>
        <div style={styles.footerContent}>
          <div style={styles.footerLeft}>
            <Typography variant="h6" style={styles.siteName}>Farmacinha - Saúde & Bem-Estar</Typography>
            <div style={styles.socialIcons}>
              <a href="#"><img src="icon-facebook.svg" alt="Facebook" /></a>
              <a href="#"><img src="icon-linkedin.svg" alt="LinkedIn" /></a>
              <a href="#"><img src="icon-youtube.svg" alt="YouTube" /></a>
            </div>
          </div>
          <div style={styles.footerGrid}>
            <div style={styles.footerColumn}>
              <Typography variant="h6">Serviços</Typography>
              <Typography variant="body1"><a href="#">Consultas Online</a></Typography>
              <Typography variant="body1"><a href="#">Prescrições</a></Typography>
              <Typography variant="body1"><a href="#">Medicamentos</a></Typography>
            </div>
            <div style={styles.footerColumn}>
              <Typography variant="h6">Ajuda</Typography>
              <Typography variant="body1"><a href="#">Central de Ajuda</a></Typography>
              <Typography variant="body1"><a href="#">Fale Conosco</a></Typography>
              <Typography variant="body1"><a href="#">Dúvidas Frequentes</a></Typography>
            </div>
            <div style={styles.footerColumn}>
              <Typography variant="h6">Empresa</Typography>
              <Typography variant="body1"><a href="#">Sobre Nós</a></Typography>
              <Typography variant="body1"><a href="#">Carreiras</a></Typography>
              <Typography variant="body1"><a href="#">Política de Privacidade</a></Typography>
            </div>
          </div>
        </div>
      </footer>
    </div>
  );
};

