import React, { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { Alert, Modal, Box, IconButton } from "@mui/material";
import CheckIcon from '@mui/icons-material/Check'; // Ícone de confirmação
import CloseIcon from '@mui/icons-material/Close'; // Ícone de fechar

const Cadastro = () => {
  const [view, setView] = useState(""); // "cliente", "atendente" ou vazio
  const [formData, setFormData] = useState({
    nome: "",
    cnpj: "",
    cpf: "",
    email: "",
    senha: "",
  });
  const [cnpjValid, setCnpjValid] = useState(null); // Estado para validação de CNPJ
  const [alertMessage, setAlertMessage] = useState(""); // Estado para mensagem do alerta
  const [openModal, setOpenModal] = useState(false); // Estado para abrir/fechar o modal
  const [modalMessage, setModalMessage] = useState(""); // Mensagem do modal
  const [cnpjCheckLoading, setCnpjCheckLoading] = useState(false); // Carregando o status da verificação
  const navigate = useNavigate(); // Inicializando o hook useNavigate

  // Animação dos containers
  const containerVariant = {
    hidden: { opacity: 0, scale: 0.9 },
    visible: { opacity: 1, scale: 1, transition: { duration: 0.5 } },
    exit: { opacity: 0, scale: 0.9, transition: { duration: 0.5 } },
  };

  const handleBack = () => {
    setView(""); // Voltar à tela inicial
    setFormData({ nome: "", cnpj: "", cpf: "", email: "", senha: "" });
    setCnpjValid(null); // Resetar o estado de validação do CNPJ
    setAlertMessage(""); // Limpar a mensagem do alerta
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });

    // Validação de CNPJ no campo de CNPJ
    if (name === "cnpj" && value.length === 14) {
      axios.get(`https://api.cnpja.com/office/${value}`, {
        headers: { Authorization: '507f5121-2175-4925-8b5b-5f4ff17b3312-3165bba4-7fe0-461b-8092-5b90c99c4088' }
      })
        .then((response) => {
          setCnpjValid(true); // CNPJ válido
          setAlertMessage("CNPJ válido!");
        })
        .catch((error) => {
          setCnpjValid(false); // CNPJ inválido
          setAlertMessage("CNPJ inválido!");
        });
    }
  };

  const handleSubmit = async (type) => {
    const url =
      type === "Atendente"
        ? "http://127.0.0.1:8000/api/registro/atendente/"
        : "http://127.0.0.1:8000/api/registro/cliente/";

    try {
      const response = await axios.post(url, formData);
      alert(`${type} registrado com sucesso!`);
      console.log("Response:", response.data);
      handleBack(); // Resetar o formulário
      navigate("/login");
    } catch (error) {
      console.error("Erro ao enviar:", error.response?.data || error.message);
      alert("Erro ao registrar. Verifique os dados e tente novamente.");
    }
  };

  // Função para verificar CNPJ e abrir o modal de sucesso
  const handleVerifyCnpj = () => {
    setCnpjCheckLoading(true);
    axios.get(`https://api.cnpja.com/office/${formData.cnpj}`, {
      headers: { Authorization: '507f5121-2175-4925-8b5b-5f4ff17b3312-3165bba4-7fe0-461b-8092-5b90c99c4088' }
    })
      .then(() => {
        setCnpjValid(true);
        setModalMessage("CNPJ Verificado e existe");
        setOpenModal(true); // Abre o modal de sucesso
        setTimeout(() => {
          setOpenModal(false); // Fecha o modal após 2 segundos
        }, 2000);
      })
      .catch(() => {
        setCnpjValid(false);
        setModalMessage("CNPJ não encontrado.");
        setOpenModal(true); // Abre o modal de erro
        setTimeout(() => {
          setOpenModal(false); // Fecha o modal após 2 segundos
        }, 2000);
      })
      .finally(() => {
        setCnpjCheckLoading(false); // Finaliza o estado de carregamento
      });
  };

  return (
    <div style={styles.container}>
      {/* Navbar */}
      <header style={styles.navbar}>
        <div style={styles.logo}>
          <h1 style={styles.logoText}>Farmacinha</h1>
        </div>
        <nav>
          <ul style={styles.navLinks}>
            <li>
              <a href="/" style={styles.navLink}>Início</a>
            </li>
            <li>
              <a href="/cadastro" style={styles.navLink}>Cadastre-se</a>
            </li>
            <li>
              <a href="/login" style={styles.navLink}>Entrar</a>
            </li>
          </ul>
        </nav>
      </header>

      {/* Conteúdo */}
      <div style={styles.content}>
        <AnimatePresence>
          {/* Título e Subtítulo */}
          <div style={styles.titleContainer}>
            <h1 style={styles.mainTitle}>Farmacinha</h1>
            <h2 style={styles.subtitle}>Formulário de Cadastro</h2>
          </div>

          {/* Tela de Escolha */}
          {view === "" && (
            <motion.div
              style={styles.choiceContainer}
              variants={containerVariant}
              initial="hidden"
              animate="visible"
              exit="exit"
            >
              <h1 style={styles.title}>Você quer se registrar como?</h1>
              <div style={styles.options}>
                <motion.div
                  style={styles.optionBox}
                  whileHover={{ scale: 1.1 }}
                  whileTap={{ scale: 0.95 }}
                  onClick={() => setView("atendente")}
                >
                  Atendente
                </motion.div>
                <motion.div
                  style={styles.optionBox}
                  whileHover={{ scale: 1.1 }}
                  whileTap={{ scale: 0.95 }}
                  onClick={() => setView("cliente")}
                >
                  Cliente
                </motion.div>
              </div>
            </motion.div>
          )}

          {/* Formulário de Atendente */}
          {view === "atendente" && (
            <motion.div
              style={styles.formContainer}
              variants={containerVariant}
              initial="hidden"
              animate="visible"
              exit="exit"
            >
              <h2 style={styles.formTitle}>Registro de Atendente</h2>
              <input
                type="text"
                placeholder="Nome Completo"
                name="nome"
                value={formData.nome}
                onChange={handleChange}
                style={styles.input}
              />
              <div style={styles.cnpjContainer}>
                <input
                  type="text"
                  placeholder="CNPJ"
                  name="cnpj"
                  value={formData.cnpj}
                  onChange={handleChange}
                  style={styles.input}
                />
                <button
                  style={styles.verifyButton}
                  onClick={handleVerifyCnpj}
                  disabled={cnpjCheckLoading}
                >
                  {cnpjCheckLoading ? "Verificando..." : "Verificar CNPJ"}
                </button>
              </div>
              {alertMessage && (
                <Alert icon={<CheckIcon fontSize="inherit" />} severity={cnpjValid ? "success" : "error"}>
                  {alertMessage}
                </Alert>
              )}
              <input
                type="email"
                placeholder="E-mail"
                name="email"
                value={formData.email}
                onChange={handleChange}
                style={styles.input}
              />
              <input
                type="password"
                placeholder="Senha"
                name="senha"
                value={formData.senha}
                onChange={handleChange}
                style={styles.input}
              />
              <div style={styles.btnGroup}>
                <button style={styles.btnCancel} onClick={handleBack}>
                  Voltar
                </button>
                <button
                  style={styles.btnSubmit}
                  onClick={() => handleSubmit("Atendente")}
                >
                  Registrar
                </button>
              </div>
            </motion.div>
          )}

          {/* Formulário de Cliente */}
          {view === "cliente" && (
            <motion.div
              style={styles.formContainer}
              variants={containerVariant}
              initial="hidden"
              animate="visible"
              exit="exit"
            >
              <h2 style={styles.formTitle}>Registro de Cliente</h2>
              <input
                type="text"
                placeholder="Nome Completo"
                name="nome"
                value={formData.nome}
                onChange={handleChange}
                style={styles.input}
              />
              <div style={styles.cnpjContainer}>
                <input
                  type="text"
                  placeholder="CNPJ"
                  name="cnpj"
                  value={formData.cnpj}
                  onChange={handleChange}
                  style={styles.input}
                />
                <button
                  style={styles.verifyButton}
                  onClick={handleVerifyCnpj}
                  disabled={cnpjCheckLoading}
                >
                  {cnpjCheckLoading ? "Verificando..." : "Verificar CNPJ"}
                </button>
              </div>
              {alertMessage && (
                <Alert icon={<CheckIcon fontSize="inherit" />} severity={cnpjValid ? "success" : "error"}>
                  {alertMessage}
                </Alert>
              )}
              <input
                type="email"
                placeholder="E-mail"
                name="email"
                value={formData.email}
                onChange={handleChange}
                style={styles.input}
              />
              <input
                type="password"
                placeholder="Senha"
                name="senha"
                value={formData.senha}
                onChange={handleChange}
                style={styles.input}
              />
              <div style={styles.btnGroup}>
                <button style={styles.btnCancel} onClick={handleBack}>
                  Voltar
                </button>
                <button
                  style={styles.btnSubmit}
                  onClick={() => handleSubmit("Cliente")}
                >
                  Registrar
                </button>
              </div>
            </motion.div>
          )}
        </AnimatePresence>

        {/* Modal */}
        <Modal
          open={openModal}
          onClose={() => setOpenModal(false)}
          aria-labelledby="modal-title"
          aria-describedby="modal-description"
        >
          <Box sx={styles.modalBox}>
            <div style={styles.modalContent}>
              <motion.div
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ duration: 0.3 }}
              >
                <h2>{modalMessage}</h2>
                <motion.div
                  initial={{ scale: 0 }}
                  animate={{ scale: 1 }}
                  transition={{ duration: 0.5 }}
                >
                  <IconButton style={{ color: "green" }}>
                    <CheckIcon />
                  </IconButton>
                </motion.div>
              </motion.div>
            </div>
          </Box>
        </Modal>
      </div>
    </div>
  );
};

const styles = {
  container: {
    fontFamily: "Arial, sans-serif",
    padding: "0 20px",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    minHeight: "100vh",
  },
  navbar: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    padding: "10px 20px",
    backgroundColor: "#4CAF50",
    color: "#fff",
    position: "fixed",
    top: 0,
    left: 0,
    right: 0,
    zIndex: 10,
  },
  logo: {
    display: "flex",
    alignItems: "center",
  },
  logoText: {
    fontSize: "24px",
    fontWeight: "bold",
    color: "#fff",
  },
  navLinks: {
    display: "flex",
    listStyle: "none",
  },
  navLink: {
    color: "#fff",
    textDecoration: "none",
    marginLeft: "20px",
    fontSize: "16px",
  },
  content: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    marginTop: "120px",
    width: "100%",
  },
  titleContainer: {
    textAlign: "center",
    marginBottom: "40px",
  },
  mainTitle: {
    fontSize: "36px",
    fontWeight: "bold",
    color: "#333",
  },
  subtitle: {
    fontSize: "24px",
    fontWeight: "normal",
    color: "red",
  },
  formContainer: {
    width: "80%",
    maxWidth: "500px",
    padding: "20px",
    backgroundColor: "#fff",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
    borderRadius: "8px",
  },
  formTitle: {
    fontSize: "24px",
    fontWeight: "bold",
    textAlign: "center",
    marginBottom: "20px",
  },
  input: {
    width: "100%",
    padding: "12px",
    marginBottom: "15px",
    borderRadius: "4px",
    border: "1px solid #ddd",
    fontSize: "16px",
  },
  verifyButton: {
    backgroundColor: "#2196F3",
    color: "#fff",
    padding: "10px 20px",
    borderRadius: "4px",
    fontSize: "16px",
    cursor: "pointer",
  },
  cnpjContainer: {
    display: "flex",
    alignItems: "center",
    marginBottom: "15px",
  },
  modalBox: {
    position: "absolute",
    top: "50%",
    left: "50%",
    transform: "translate(-50%, -50%)",
    backgroundColor: "#fff",
    padding: "30px",
    borderRadius: "8px",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
  },
  modalContent: {
    textAlign: "center",
  },
};

export default Cadastro;
