import React, { useState } from "react";
import { Link } from "react-router-dom";
import Modal from "react-modal";
import { GoogleGenerativeAI } from "@google/generative-ai";
import {
  Button,
  TextField,
  Typography,
  Box,
  AppBar,
  Toolbar,
  CircularProgress,
} from "@mui/material";
import { BounceLoader } from "react-spinners";
import farmaciaImage from "../Media/farmacia.jpeg"; // Certifique-se de que o caminho da imagem está correto

// Configuração do Modal
Modal.setAppElement("#root");

const Home = () => {
  const [medicamento, setMedicamento] = useState("");
  const [resultado, setResultado] = useState(null);
  const [isOpen, setIsOpen] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const openModal = () => setIsOpen(true);
  const closeModal = () => setIsOpen(false);

  const handleSearch = async () => {
    if (!medicamento.trim()) {
      setError("Por favor, digite o nome de um medicamento.");
      return;
    }
    setError(null);
    setLoading(true);

    try {
      const genAI = new GoogleGenerativeAI("AIzaSyDRhqrsJafsJMUMKo61Sh2aUp4mhWtOvwk");
      const model = genAI.getGenerativeModel({ model: "gemini-1.5-flash" });
      const prompt = `Conte-me tudo sobre o medicamento ${medicamento}.`;
      const result = await model.generateContent(prompt);

      setResultado(result.response.text());
      openModal();
    } catch (err) {
      setError("Erro ao buscar informações. Verifique sua conexão ou chave.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <Box sx={{ fontFamily: "Arial, sans-serif", backgroundColor: "#f9f9f9", minHeight: "100vh" }}>
      {/* Navbar */}
      <AppBar position="fixed" color="primary">
        <Toolbar>
          <Typography variant="h6" sx={{ flexGrow: 1 }}>
            Saúde & Bem-Estar
          </Typography>
          <Link to="/login" style={{ color: "#fff", textDecoration: "none", marginRight: "20px" }}>
            Entrar
          </Link>
          <Link to="/cadastro" style={{ color: "#fff", textDecoration: "none" }}>
            Cadastre-se
          </Link>
        </Toolbar>
      </AppBar>

      {/* Hero Section */}
      <Box
        sx={{
          textAlign: "center",
          padding: "100px 20px",
          marginTop: "80px",
        }}
      >
        <Typography variant="h1" sx={{ fontWeight: "bold", fontSize: "48px" }}>
          Farmacinha
        </Typography>
        <Typography variant="h2" sx={{ color: "green", fontSize: "36px" }}>
          Saúde & Bem-Estar
        </Typography>
        <Typography variant="h5" sx={{ marginTop: "20px" }}>
          Cuidando de Você com Clareza e Confiança
        </Typography>
        <Typography variant="body1" sx={{ marginTop: "10px", fontStyle: "italic" }}>
          Dúvidas sobre medicamentos ou prescrições? Encontre orientações e respostas com segurança
          aqui.
        </Typography>
      </Box>

      {/* Barra de Pesquisa */}
      <Box sx={{ textAlign: "center", marginTop: "30px" }}>
        <Typography variant="h4" sx={{ marginBottom: "20px" }}>
          Qual o remédio você tem interesse em saber?
        </Typography>
        <TextField
          label="Digite o nome do remédio"
          variant="outlined"
          value={medicamento}
          onChange={(e) => setMedicamento(e.target.value)}
          sx={{ width: "300px", marginRight: "10px" }}
        />
        <Button variant="contained" onClick={handleSearch}>
          Buscar
        </Button>
        {error && (
          <Typography variant="body1" color="error" sx={{ marginTop: "10px" }}>
            {error}
          </Typography>
        )}
      </Box>

      {/* Modal com as Informações do Medicamento */}
      <Modal
        isOpen={isOpen}
        onRequestClose={closeModal}
        style={{
          content: {
            top: "50%",
            left: "50%",
            transform: "translate(-50%, -50%)",
            width: "500px",
            height: "500px",
            borderRadius: "8px",
            padding: "20px",
            overflowY: "auto",
          },
          overlay: {
            backgroundColor: "rgba(0, 0, 0, 0.5)",
          },
        }}
      >
        {loading ? (
          <Box sx={{ textAlign: "center", marginTop: "100px" }}>
            <CircularProgress color="primary" />
            <Typography variant="h6" sx={{ marginTop: "20px" }}>
              Carregando informações...
            </Typography>
          </Box>
        ) : (
          <Box>
            <Typography
              variant="h4"
              sx={{ fontWeight: "bold", marginBottom: "20px", textAlign: "center" }}
            >
              Informações sobre {medicamento}
            </Typography>
            {resultado ? (
              <Typography variant="body1" sx={{ whiteSpace: "pre-wrap" }}>
                {resultado}
              </Typography>
            ) : (
              <Typography variant="body1">Informações não encontradas.</Typography>
            )}
            <Button
              onClick={closeModal}
              variant="contained"
              sx={{ marginTop: "20px", display: "block", marginLeft: "auto", marginRight: "auto" }}
            >
              Fechar
            </Button>
          </Box>
        )}
      </Modal>

      {/* Imagem */}
      <Box sx={{ textAlign: "center", marginTop: "50px" }}>
        <img
          src={farmaciaImage}
          alt="Farmácia"
          style={{ maxWidth: "100%", height: "auto", borderRadius: "10px" }}
        />
      </Box>

      {/* Footer */}
      <footer style={styles.footer}>
