import React, { useState, useEffect } from "react";
import { TextField, Button, Typography, Box, AppBar, Toolbar, CircularProgress, Modal, Menu, MenuItem, Avatar } from "@mui/material";
import atendenteImage from "../Media/image.jpg"; // Imagem do farmacinha

// Estilos do Modal
const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: 600,  // Tamanho maior para o modal
  height: 600, // Definindo a altura do modal
  bgcolor: 'background.paper',
  border: '2px solid #000',
  boxShadow: 24,
  p: 4,
  overflowY: "auto", // Permitindo rolagem se o conteúdo for maior
};

const PacientePage = () => {
  const [medicamento, setMedicamento] = useState(""); // Armazenar o nome do medicamento
  const [loading, setLoading] = useState(false); // Estado de carregamento
  const [error, setError] = useState(null); // Estado de erro
  const [open, setOpen] = useState(false); // Controle do modal
  const [medicamentoInfo, setMedicamentoInfo] = useState(""); // Informações do medicamento
  const [anchorEl, setAnchorEl] = useState(null); // Controle do menu suspenso
  const [nomePaciente, setNomePaciente] = useState(""); // Nome do paciente (inicialmente vazio)

  // Função que chama a API para buscar os dados do paciente
  const fetchPacienteData = async () => {
    try {
      const response = await fetch("/api/usuario/perfil/", {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${localStorage.getItem("auth_token")}`, // Adiciona o token JWT
        },
      });

      if (!response.ok) {
        throw new Error("Erro ao carregar dados do paciente");
      }

      const data = await response.json();
      setNomePaciente(data.nome); // Supondo que o nome do paciente esteja na resposta como `data.nome`
    } catch (error) {
      console.error("Erro ao buscar dados do paciente:", error);
      setError("Erro ao carregar dados do paciente");
    }
  };

  // Carregar dados do paciente assim que o componente for montado
  useEffect(() => {
    fetchPacienteData();
  }, []); // A dependência vazia faz com que a função seja chamada apenas uma vez, quando o componente for montado

  const handleMenuClick = (event) => setAnchorEl(event.currentTarget);
  const handleMenuClose = () => setAnchorEl(null);

  // Função para formatar as informações do medicamento
  const formatMedicamentoInfo = (info) => {
    return info
      .replace(/(\d+\.)/g, "<strong>$&</strong>") // Deixa os números das listas em negrito
      .replace(/([A-Za-z ]+)(?=:)/g, "<strong>$1</strong>"); // Deixa os títulos (ex: "Classe Terapêutica") em negrito
  };

  // Função de busca do medicamento e integração com o Gemini
  const handleSearch = async () => {
    if (!medicamento.trim()) {
      setError("Por favor, digite o nome de um medicamento.");
      return;
    }

    setError(null);
    setLoading(true); // Ativa o carregamento
    setOpen(true); // Abre o modal

    try {
      // Aqui você pode adicionar a integração com a API do Gemini ou uma outra lógica para buscar informações
      // de medicamentos
      const genAI = new GoogleGenerativeAI("Sua_API_KEY_Aqui");
      const model = genAI.getGenerativeModel({ model: "gemini-1.5-flash" });

      // Prompt que você passará para o modelo de IA
      const prompt = `Conte-me em 20 linhas tudo separadinho em tópicos sobre o medicamento: ${medicamento}.`;

      // Fazendo a chamada à API do Gemini para obter informações sobre o medicamento
      const result = await model.generateContent(prompt);
      const formattedInfo = formatMedicamentoInfo(result.response.text()); // Formata as informações
      setMedicamentoInfo(formattedInfo); // Armazenando as informações no estado
    } catch (error) {
      setError("Erro ao buscar informações do medicamento.");
      console.error("Erro ao buscar medicamento:", error);
    } finally {
      setLoading(false); // Desativa o carregamento
    }
  };

  // Função de logout
  const handleLogout = () => {
    localStorage.removeItem("auth_token"); // Limpa o token de autenticação
    window.location.href = '/'; // Redireciona para a página inicial
  };

  return (
    <Box>
      {/* Navbar */}
      <AppBar position="fixed">
        <Toolbar>
          <Typography variant="h6">
            Paciente: {nomePaciente || "Carregando..."}
          </Typography>

          {/* Menu suspenso */}
          <Box>
            <Button
              aria-controls="menu-appbar"
              aria-haspopup="true"
              onClick={handleMenuClick}
            >
              <Avatar sx={{ width: 32, height: 32, marginRight: 1 }} /> {/* Icone do usuário */}
              {nomePaciente || "Carregando..."}
            </Button>

            <Menu
              anchorEl={anchorEl}
              open={Boolean(anchorEl)}
              onClose={handleMenuClose}
            >
              <MenuItem onClick={() => alert("Abrir perfil")}>Perfil</MenuItem>
              <MenuItem onClick={handleLogout}>Sair</MenuItem>
            </Menu>
          </Box>
        </Toolbar>
      </AppBar>

      {/* Hero Section */}
      <Box sx={{ textAlign: "center", padding: "120px 20px 40px", maxWidth: "800px", margin: "0 auto" }}>
        <Typography variant="h4">Paciente</Typography>
        <Typography variant="body1">Encontre informações sobre medicamentos e acesse os seus dados.</Typography>

        {/* Barra de Pesquisa */}
        <Box sx={{ textAlign: "center", marginTop: "20px", maxWidth: "1200px", margin: "0 auto" }}>
          <TextField
            label="Digite o nome do medicamento"
            variant="outlined"
            value={medicamento}
            onChange={(e) => setMedicamento(e.target.value)}
          />
          <Button variant="contained" color="primary" onClick={handleSearch} disabled={loading}>
            {loading ? <CircularProgress size={24} /> : "Pesquisar"}
          </Button>
        </Box>
      </Box>

      {/* Modal com as Informações do Medicamento */}
      <Modal open={open} onClose={() => setOpen(false)} aria-labelledby="modal-title" aria-describedby="modal-description">
        <Box sx={style}>
          {loading ? (
            <CircularProgress size={60} sx={{ display: "block", margin: "0 auto" }} />
          ) : (
            <div>
              <Typography variant="h4">Informações sobre {medicamento}</Typography>
              <Typography id="modal-description" variant="body1" dangerouslySetInnerHTML={{ __html: medicamentoInfo }} />
            </div>
          )}
        </Box>
      </Modal>
    </Box>
  );
};

export default PacientePage;
