import React, { useState } from "react";
import farmaciaImage from "../Media/farmacia.jpeg"; // Importando a imagem
import { Link } from "react-router-dom";
import Modal from "react-modal"; // Importando o Modal
import axios from "axios"; // Importando o axios para requisições

// Definir o modal para se ancorar no HTML
Modal.setAppElement("#root");

const Home = () => {
  const [medicamento, setMedicamento] = useState(""); // Estado para armazenar o nome do medicamento
  const [resultado, setResultado] = useState(null); // Estado para armazenar o resultado da busca
  const [isOpen, setIsOpen] = useState(false); // Controle de abertura/fechamento do modal
  const [loading, setLoading] = useState(false); // Estado de carregamento

  // Função para abrir o modal
  const openModal = () => setIsOpen(true);

  // Função para fechar o modal
  const closeModal = () => setIsOpen(false);

  // Função para buscar o medicamento com a IA do Gemini
  const handleSearch = async () => {
    if (!medicamento) return; // Verifica se o campo de pesquisa não está vazio

    setLoading(true); // Ativa o carregamento enquanto a busca está sendo feita

    try {
      // Certifique-se de substituir sua chave de API
      const response = await axios.post(
        "https://generativelanguage.googleapis.com/v1beta/models/gemini-1.5-flash-latest:generateContent", // Endpoint da API Gemini
        {
          contents: [
            {
              parts: [
                {
                  text: `Conte-me tudo sobre o medicamento ${medicamento}.`
                }
              ]
            }
          ]
        },
        {
          headers: {
            "Content-Type": "application/json",
            // Chave da API (já fornecida por você)
            "Authorization": `Bearer AIzaSyDRhqrsJafsJMUMKo61Sh2aUp4mhWtOvwk`, 
          },
        }
      );

      // Atualiza o estado com o resultado da busca
      setResultado(response.data); // Coloca o resultado da API no estado

      openModal(); // Abre o modal para mostrar o resultado
    } catch (error) {
      console.error("Erro ao buscar medicamento:", error.response || error);
      alert("Erro ao buscar medicamento. Verifique a chave da API.");
    } finally {
      setLoading(false); // Desativa o carregamento
    }
  };

  return (
    <div style={styles.container}>
      {/* Navbar */}
      <header style={styles.navbar}>
        <div style={styles.logo}>
          <h1 style={styles.logoText}>Saúde&<br />Bem-Estar</h1>
        </div>
        <nav>
          <ul style={styles.navLinks}>
            <li><Link to="/login" style={styles.navLink}>Entrar</Link></li>
            <li><Link to="/cadastro" style={styles.navLink}>Cadastre-se</Link></li>
          </ul>
        </nav>
      </header>

      {/* Hero Section */}
      <section style={styles.hero}>
        <h1 style={styles.heroTitle}>Farmacinha</h1>
        <h2 style={styles.heroSubtitle}>Saúde & Bem-Estar</h2>
        <p style={styles.heroText}>Cuidando de Você com Clareza e Confiança</p>
        <p style={styles.heroSubtext}>Dúvidas sobre medicamentos ou prescrições? Encontre orientações e respostas com segurança aqui.</p>
      </section>

      {/* Barra de Pesquisa */}
      <section style={styles.searchSection}>
        <h2 style={styles.searchTitle}>Qual o remédio você tem interesse em saber?</h2>
        <input
          type="text"
          placeholder="Digite o nome do remédio"
          value={medicamento}
          onChange={(e) => setMedicamento(e.target.value)}
          style={styles.searchInput}
          onKeyPress={(e) => e.key === "Enter" && handleSearch()} // Busca ao pressionar Enter
        />
        <button onClick={handleSearch} style={styles.searchButton}>Buscar</button>
      </section>

      {/* Image Section */}
      <section style={styles.imageSection}>
        <img src={farmaciaImage} alt="Farmácia" style={styles.heroImage} />
      </section>

      {/* Modal com as Informações do Medicamento */}
      <Modal
        isOpen={isOpen}
        onRequestClose={closeModal}
        style={customStyles}
        contentLabel="Informações do Medicamento"
      >
        {loading ? (
          <div>Carregando...</div>
        ) : (
          <div>
            {resultado ? (
              <>
                <h2>{resultado.response}</h2> {/* Ajuste conforme a resposta do modelo */}
                <p><strong>Detalhes:</strong> {resultado.details}</p>
                <p><strong>Dosagem:</strong> {resultado.dosage}</p>
                <p><strong>Efeitos Colaterais:</strong> {resultado.side_effects}</p>
              </>
            ) : (
              <p>Não foi possível encontrar informações sobre este medicamento.</p>
            )}
            <button onClick={closeModal} style={styles.closeButton}>Fechar</button>
          </div>
        )}
      </Modal>

      {/* Footer */}
      <footer style={styles.footer}>
        <div style={styles.footerContent}>
          <div style={styles.footerLeft}>
            <p style={styles.siteName}>Farmacinha - Saúde & Bem-Estar</p>
            <div style={styles.socialIcons}>
              <a href="#"><img src="icon-facebook.svg" alt="Facebook" /></a>
              <a href="#"><img src="icon-linkedin.svg" alt="LinkedIn" /></a>
              <a href="#"><img src="icon-youtube.svg" alt="YouTube" /></a>
            </div>
          </div>
          <div style={styles.footerGrid}>
            <div style={styles.footerColumn}>
              <h4>Serviços</h4>
              <p><a href="#">Consultas Online</a></p>
              <p><a href="#">Prescrições</a></p>
              <p><a href="#">Medicamentos</a></p>
            </div>
            <div style={styles.footerColumn}>
              <h4>Ajuda</h4>
              <p><a href="#">Central de Ajuda</a></p>
              <p><a href="#">Fale Conosco</a></p>
              <p><a href="#">Dúvidas Frequentes</a></p>
            </div>
            <div style={styles.footerColumn}>
              <h4>Empresa</h4>
              <p><a href="#">Sobre Nós</a></p>
              <p><a href="#">Carreiras</a></p>
              <p><a href="#">Política de Privacidade</a></p>
            </div>
          </div>
        </div>
      </footer>
    </div>
  );
};

