import React, { useState } from 'react';
import { TextField, Button, CircularProgress, Typography, Box, Modal } from '@mui/material';
import { GoogleGenerativeAI } from '@google/generative-ai';  // Import Gemini AI

const BularioPage = () => {
  const [medicamento, setMedicamento] = useState('');
  const [loading, setLoading] = useState(false);
  const [medicamentoInfo, setMedicamentoInfo] = useState('');
  const [error, setError] = useState(null);
  const [open, setOpen] = useState(false);

  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);

  const handleSearch = async () => {
    if (!medicamento.trim()) {
      setError('Por favor, digite o nome de um medicamento.');
      return;
    }

    setError(null);
    setLoading(true);
    handleOpen();

    try {
      const genAI = new GoogleGenerativeAI("YOUR_API_KEY");  // Add your API key
      const model = genAI.getGenerativeModel({ model: "gemini-1.5-flash" });

      const prompt = `Conte-me em 20 linhas tudo separadinho em tópicos sobre o medicamento: ${medicamento}.`;

      const result = await model.generateContent(prompt);
      setMedicamentoInfo(result.response.text());
    } catch (err) {
      setError('Erro ao buscar informações do medicamento.');
    } finally {
      setLoading(false);
      handleClose();
    }
  };

  return (
    <Box sx={{ fontFamily: 'Arial, sans-serif', backgroundColor: '#f9f9f9', minHeight: '100vh' }}>
      {/* Header Section */}
      <header style={{ padding: '20px 40px', borderBottom: '1px solid #ddd' }}>
        <Typography variant="h4" style={{ fontSize: '36px', fontWeight: 'bold', color: '#333' }}>Farmacinha</Typography>
        <Typography variant="h6" style={{ fontSize: '22px', color: '#d81b1b' }}>Saúde & Bem-Estar</Typography>
      </header>

      {/* Search Section */}
      <Box sx={{ padding: '20px 40px', textAlign: 'center' }}>
        <TextField
          label="Digite o nome do medicamento"
          variant="outlined"
          value={medicamento}
          onChange={(e) => setMedicamento(e.target.value)}
          sx={{ width: '80%' }}
        />
        <Button
          variant="contained"
          color="primary"
          onClick={handleSearch}
          disabled={loading}
          sx={{ fontWeight: 'bold', padding: '10px 20px', fontSize: '18px', marginTop: '20px' }}
        >
          {loading ? <CircularProgress size={24} sx={{ color: 'white' }} /> : 'Buscar'}
        </Button>
      </Box>

      {/* Modal for loading state */}
      <Modal open={open} onClose={handleClose}>
        <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
          <CircularProgress size={50} />
        </Box>
      </Modal>

      {/* Display Medication Info */}
      {medicamentoInfo && (
        <Box sx={{ padding: '20px 40px' }}>
          <Typography variant="h5" sx={{ marginBottom: '20px' }}>Informações sobre {medicamento}</Typography>
          <Typography variant="body1" sx={{ fontSize: '16px', lineHeight: '1.6' }}>
            {medicamentoInfo}
          </Typography>
        </Box>
      )}

      {/* Footer */}
      <footer style={{ backgroundColor: '#f9f9f9', textAlign: 'center', padding: '20px 0', borderTop: '1px solid #ddd' }}>
        <div>
          <a href="#">Facebook</a> | <a href="#">LinkedIn</a> | <a href="#">Instagram</a>
        </div>
      </footer>
    </Box>
  );
};

export default BularioPage;
