import React, { useState } from 'react';

const RedefinirSenhaPage = () => {
  const [cpf, setCpf] = useState('');
  const [message, setMessage] = useState('');

  // Função para lidar com a mudança no campo de CPF
  const handleCpfChange = (event) => {
    const value = event.target.value;
    // Permite apenas números e limita o CPF a 11 dígitos
    if (/^\d{0,11}$/.test(value)) {
      setCpf(value);
    }
  };

  // Função para enviar a solicitação de redefinição de senha
  const handleEnviar = (event) => {
    event.preventDefault();
    // Aqui você pode implementar o envio do e-mail no backend
    setMessage(`Instruções enviadas para o e-mail associado ao CPF ${cpf}.`);
  };

  // Função para cancelar e redirecionar para a página home
  const handleCancelar = () => {
    window.location.href = '/'; // Redireciona para a página home
  };

  // Estilos inline
  const styles = {
    body: {
      fontFamily: 'Arial, sans-serif',
      backgroundColor: '#f4f4f4',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      height: '100vh',
      margin: 0,
    },
    container: {
      backgroundColor: 'white',
      padding: '20px',
      borderRadius: '8px',
      boxShadow: '0 4px 10px rgba(0, 0, 0, 0.1)',
      textAlign: 'center',
      width: '300px',
    },
    h2: {
      fontSize: '24px',
      marginBottom: '20px',
    },
    label: {
      fontSize: '14px',
      marginBottom: '5px',
      display: 'block',
    },
    input: {
      width: '100%',
      padding: '10px',
      marginBottom: '20px',
      border: '1px solid #ccc',
      borderRadius: '4px',
      fontSize: '16px',
    },
    buttons: {
      display: 'flex',
      justifyContent: 'space-between',
    },
    button: {
      padding: '10px 20px',
      border: 'none',
      borderRadius: '4px',
      cursor: 'pointer',
      fontSize: '16px',
    },
    enviar: {
      backgroundColor: '#28a745',
      color: 'white',
    },
    cancelar: {
      backgroundColor: '#dc3545',
      color: 'white',
    },
    message: {
      marginTop: '20px',
      fontSize: '14px',
      color: '#28a745',
    },
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.h2}>Redefinir Senha</h2>
      <form onSubmit={handleEnviar}>
        <label htmlFor="cpf" style={styles.label}>CPF:</label>
        <input
          type="text"
          id="cpf"
          name="cpf"
          value={cpf}
          onChange={handleCpfChange}
          maxLength="11"
          required
          style={styles.input}
        />
        <div style={styles.buttons}>
          <button type="submit" style={{ ...styles.button, ...styles.enviar }}>Enviar</button>
          <button type="button" onClick={handleCancelar} style={{ ...styles.button, ...styles.cancelar }}>Cancelar</button>
        </div>
      </form>
      {message && <p style={styles.message}>{message}</p>}
    </div>
  );
};

export default RedefinirSenhaPage;
