import React, { useState, useEffect } from "react";
import { TextField, Button, Typography, Box, AppBar, Toolbar, CircularProgress, Modal, Menu, MenuItem, Avatar } from "@mui/material";

const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: 600, 
  height: 600, 
  bgcolor: 'background.paper',
  border: '2px solid #000',
  boxShadow: 24,
  p: 4,
  overflowY: "auto",
};

const PacientePage = () => {
  const [medicamento, setMedicamento] = useState(""); 
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [open, setOpen] = useState(false);
  const [medicamentoInfo, setMedicamentoInfo] = useState("");
  const [anchorEl, setAnchorEl] = useState(null);
  const [nomePaciente, setNomePaciente] = useState(""); 

  const fetchPacienteData = async () => {
    try {
      const token = document.cookie.split(';').find(row => row.startsWith('auth_token')).split('=')[1];

      if (!token) {
        throw new Error("Token de autenticação não encontrado.");
      }

      const response = await fetch("http://127.0.0.1:8000/api/usuario/perfil/", {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${token}`,
        },
      });

      if (!response.ok) {
        throw new Error("Erro ao carregar dados do paciente");
      }

      const data = await response.json();
      setNomePaciente(data.nome);
    } catch (error) {
      console.error("Erro ao buscar dados do paciente:", error);
      setError("Erro ao carregar dados do paciente");
    }
  };

  useEffect(() => {
    fetchPacienteData();
  }, []);

  const handleMenuClick = (event) => setAnchorEl(event.currentTarget);
  const handleMenuClose = () => setAnchorEl(null);

  const handleSearch = async () => {
    if (!medicamento.trim()) {
      setError("Por favor, digite o nome de um medicamento.");
      return;
    }

    setError(null);
    setLoading(true);
    setOpen(true);

    try {
      const result = await fetch(`http://127.0.0.1:8000/api/medicamento/${medicamento}`);
      const data = await result.json();
      setMedicamentoInfo(data);
    } catch (error) {
      setError("Erro ao buscar medicamento.");
      console.error("Erro ao buscar medicamento:", error);
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("auth_token");
    window.location.href = '/';
  };

  return (
    <Box>
      {/* Navbar */}
      <AppBar position="fixed">
        <Toolbar>
          <Typography variant="h6">
            Paciente: {nomePaciente || "Carregando..."}
          </Typography>

          {/* Menu suspenso */}
          <Box>
            <Button aria-controls="menu-appbar" aria-haspopup="true" onClick={handleMenuClick}>
              <Avatar sx={{ width: 32, height: 32, marginRight: 1 }} />
              {nomePaciente || "Carregando..."}
            </Button>

            <Menu anchorEl={anchorEl} open={Boolean(anchorEl)} onClose={handleMenuClose}>
              <MenuItem onClick={() => alert("Abrir perfil")}>Perfil</MenuItem>
              <MenuItem onClick={handleLogout}>Sair</MenuItem>
            </Menu>
          </Box>
        </Toolbar>
      </AppBar>

      {/* Hero Section */}
      <Box sx={{ textAlign: "center", padding: "120px 20px 40px", maxWidth: "800px", margin: "0 auto" }}>
        <Typography variant="h4">Paciente</Typography>
        <Typography variant="body1">Encontre informações sobre medicamentos e acesse os seus dados.</Typography>

        <Box sx={{ textAlign: "center", marginTop: "20px", maxWidth: "1200px", margin: "0 auto" }}>
          <TextField label="Digite o nome do medicamento" variant="outlined" value={medicamento} onChange={(e) => setMedicamento(e.target.value)} />
          <Button variant="contained" color="primary" onClick={handleSearch} disabled={loading}>
            {loading ? <CircularProgress size={24} /> : "Pesquisar"}
          </Button>
        </Box>
      </Box>

      <Modal open={open} onClose={() => setOpen(false)} aria-labelledby="modal-title" aria-describedby="modal-description">
        <Box sx={style}>
          {loading ? (
            <CircularProgress size={60} sx={{ display: "block", margin: "0 auto" }} />
          ) : (
            <div>
              <Typography variant="h4">Informações sobre {medicamento}</Typography>
              <Typography id="modal-description" variant="body1" dangerouslySetInnerHTML={{ __html: medicamentoInfo }} />
            </div>
          )}
        </Box>
      </Modal>
    </Box>
  );
};

export default PacientePage;
