import React, { useState } from "react";
import { Link } from "react-router-dom";
import Modal from "react-modal";
import { GoogleGenerativeAI } from "@google/generative-ai";
import {
  Button,
  TextField,
  Typography,
  Box,
  AppBar,
  Toolbar,
  CircularProgress,
} from "@mui/material";
import { BounceLoader } from "react-spinners";
import farmaciaImage from "../Media/farmacia.jpeg"; // Certifique-se de que o caminho da imagem está correto

// Configuração do Modal
Modal.setAppElement("#root");

const Home = () => {
  const [medicamento, setMedicamento] = useState("");
  const [resultado, setResultado] = useState(null);
  const [isOpen, setIsOpen] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const openModal = () => setIsOpen(true);
  const closeModal = () => setIsOpen(false);

  const handleSearch = async () => {
    if (!medicamento.trim()) {
      setError("Por favor, digite o nome de um medicamento.");
      return;
    }
    setError(null);
    setLoading(true);

    try {
      const genAI = new GoogleGenerativeAI("AIzaSyDRhqrsJafsJMUMKo61Sh2aUp4mhWtOvwk");
      const model = genAI.getGenerativeModel({ model: "gemini-1.5-flash" });
      const prompt = `Conte-me tudo sobre o medicamento ${medicamento}.`;
      const result = await model.generateContent(prompt);

      setResultado(result.response.text());
      openModal();
    } catch (err) {
      setError("Erro ao buscar informações. Verifique sua conexão ou chave.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <Box sx={styles.container}>
      {/* Navbar */}
      <AppBar position="fixed" color="primary" sx={styles.navbar}>
        <Toolbar>
          <Typography variant="h6" sx={{ flexGrow: 1 }}>
            Saúde & Bem-Estar
          </Typography>
          <Link to="/login" style={styles.navLink}>
            Entrar
          </Link>
          <Link to="/cadastro" style={styles.navLink}>
            Cadastre-se
          </Link>
        </Toolbar>
      </AppBar>

      {/* Hero Section */}
      <Box sx={styles.hero}>
        <Typography variant="h1" sx={styles.heroTitle}>
          Farmacinha
        </Typography>
        <Typography variant="h2" sx={styles.heroSubtitle}>
          Saúde & Bem-Estar
        </Typography>
        <Typography variant="h5" sx={styles.heroText}>
          Cuidando de Você com Clareza e Confiança
        </Typography>
        <Typography variant="body1" sx={styles.heroSubtext}>
          Dúvidas sobre medicamentos ou prescrições? Encontre orientações e respostas com segurança
          aqui.
        </Typography>
      </Box>

      {/* Barra de Pesquisa */}
      <Box sx={styles.searchSection}>
        <Typography variant="h4" sx={styles.searchTitle}>
          Qual o remédio você tem interesse em saber?
        </Typography>
        <TextField
          label="Digite o nome do remédio"
          variant="outlined"
          value={medicamento}
          onChange={(e) => setMedicamento(e.target.value)}
          sx={styles.searchInput}
        />
        <Button variant="contained" onClick={handleSearch} sx={styles.searchButton}>
          Buscar
        </Button>
        {error && (
          <Typography variant="body1" color="error" sx={styles.errorText}>
            {error}
          </Typography>
        )}
      </Box>

      {/* Modal com as Informações do Medicamento */}
      <Modal
        isOpen={isOpen}
        onRequestClose={closeModal}
        style={{
          content: {
            top: "50%",
            left: "50%",
            transform: "translate(-50%, -50%)",
            width: "500px",
            height: "500px",
            borderRadius: "8px",
            padding: "20px",
            overflowY: "auto",
          },
          overlay: {
            backgroundColor: "rgba(0, 0, 0, 0.5)",
          },
        }}
      >
        {loading ? (
          <Box sx={styles.loaderContainer}>
            <CircularProgress color="primary" />
            <Typography variant="h6" sx={styles.loaderText}>
              Carregando informações...
            </Typography>
          </Box>
        ) : (
          <Box sx={styles.modalContent}>
            <Typography variant="h4" sx={styles.modalTitle}>
              Informações sobre {medicamento}
            </Typography>
            <Typography variant="body1" sx={styles.modalText}>
              {resultado || "Nenhuma informação encontrada."}
            </Typography>
            <Button
              onClick={closeModal}
              variant="contained"
              sx={{ marginTop: "20px", display: "block", marginLeft: "auto", marginRight: "auto" }}
            >
              Fechar
            </Button>
          </Box>
        )}
      </Modal>

      {/* Imagem */}
      <Box sx={styles.imageSection}>
        <img src={farmaciaImage} alt="Farmácia" style={{ maxWidth: "100%", borderRadius: "10px" }} />
      </Box>

      {/* Footer */}
      <footer style={styles.footer}>
        <div style={styles.footerContent}>
          <div style={styles.footerLeft}>
            <Typography variant="h6" style={styles.siteName}>
              Farmacinha - Saúde & Bem-Estar
            </Typography>
            <div style={styles.socialIcons}>
              <a href="#">
                <img src="icon-facebook.svg" alt="Facebook" />
              </a>
              <a href="#">
                <img src="icon-linkedin.svg" alt="LinkedIn" />
              </a>
              <a href="#">
                <img src="icon-youtube.svg" alt="YouTube" />
              </a>
            </div>
          </div>
          <div style={styles.footerGrid}>
            <div style={styles.footerColumn}>
              <Typography variant="h6">Serviços</Typography>
              <Typography variant="body1">
                <a href="#">Consultas Online</a>
              </Typography>
              <Typography variant="body1">
                <a href="#">Prescrições</a>
              </Typography>
              <Typography variant="body1">
                <a href="#">Medicamentos</a>
              </Typography>
