    width: "70%", // Modal ocupando 70% da tela
    maxWidth: "800px", // Máximo de 800px
    padding: "30px",
    backgroundColor: "#fff",
    borderRadius: "8px",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.2)",
